"use strict";(function(){var b=$(".accordion-trigger");var a=$(".accordion-content");b.click(function(){if($(this).parent().find(".accordion-content").hasClass("active")){$(this).parent().find(".accordion-content").removeClass("active");return}else{a.each(function(){$(this).removeClass("active")});$(this).parent().find(".accordion-content").addClass("active")}})}());